import React, { useState, useEffect } from 'react';
import { Typography, TableContainer, Table, TableBody, TableHead, TableCell, TableRow } from '@mui/material';
import { Link, useLocation } from 'react-router-dom';
import './TopNResultPage.css';

export const TopNResultPage = () => {
    const [topN, setTopN] = useState([]);
    const location = useLocation();
    const query = new URLSearchParams(location.search);
    const n = query.get('n');

    const getTopN = async () => {
        const body = { n }
        const content = JSON.stringify(body)
        const res = await fetch('http://127.0.0.1:5000/top', {
            method: 'POST',
            body: content,
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
                'Accept': 'application/json'
            }
        }).then(response => response.json())
        setTopN(res);
    }

    useEffect(() => {
        getTopN()

    }, [])


    function createData(Term, Total_Frequencies) {
        return { Term, Total_Frequencies };
    }
    const columns = [
        { id: 'Term', label: 'Term', minWidth: 170 },
        { id: 'Total_Frenquencies', label: 'Total\u00a0Frequencies', minWidth: 170 },
    ];
    const rows = []
    function createRow() {
        topN.map((word, idx) => (rows.push(createData(word[1], word[0].toString()))))
    }
    createRow()
    console.log("===create data===", topN)
    console.log('===rows===', rows)
    return (

        <div style={{ marginTop: '5%' }}>
            <div>
                <Link to='/top_n' style={{ marginLeft: '80%', color: 'black' }}>
                    Go Back To Search
                </Link>
            </div>
            <Typography variant='h6' style={{ marginRight: '84%' }}>
                Top-N Frequent Terms
            </Typography>

            <table>
                <tbody>
                    <tr>
                        <th>Term</th>
                        <th>Frequencies</th>
                    </tr>
                    { topN.map((res, index) => (
                        <tr key={index}>
                            <td>{res[0]}</td>
                            <td>{res[1]}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div >
    )

}
